/**
 * Form validation service for contract function parameters.
 * Validates input types, ranges, and formats before submission.
 */

import type { ValidationResult, ValidationIssue } from '../types/cliValidation';
import type { FunctionParameter } from './contractInspector';
import { validateAddress, validateFunctionName as validateFnName, validateRequired, validateSorobanType } from '../utils/validators/typeValidators';
import { validateJsonObject } from '../utils/validators/formatValidators';
import { buildValidationResult } from '../utils/validators/validationResult';

/**
 * Form validation service.
 * Centralizes validation logic for simulate transaction and deploy flows.
 */
export class FormValidationService {
    /**
     * Validate Stellar contract ID (address format).
     */
    validateContractId(value: string): ValidationResult {
        return validateAddress(value);
    }

    /**
     * Validate Soroban function name.
     */
    validateFunctionName(value: string): ValidationResult {
        return validateFnName(value);
    }

    /**
     * Validate a single parameter value against its metadata.
     */
    validateParameter(value: string | undefined | null, param: FunctionParameter): ValidationResult {
        const issues: ValidationIssue[] = [];
        const field = param.name;

        // Required check
        if (param.required) {
            const requiredResult = validateRequired(value ?? '', field);
            if (!requiredResult.valid) {
                return requiredResult;
            }
        } else {
            // Optional: empty is valid
            const trimmed = value === undefined || value === null ? '' : String(value).trim();
            if (trimmed.length === 0) {
                return { valid: true, issues: [], errors: [], warnings: [] };
            }
        }

        const trimmed = String(value ?? '').trim();

        // Type validation
        if (param.type) {
            const typeResult = validateSorobanType(trimmed, param.type, field);
            if (!typeResult.valid) {
                issues.push(...typeResult.issues);
            }
        }
        // No type metadata: accept any non-empty string (JSON parsing handled at submission)

        return buildValidationResult(issues);
    }

    /**
     * Validate a batch of parameter values.
     */
    validateParameterValues(
        params: FunctionParameter[],
        values: Record<string, string>
    ): ValidationResult {
        const allIssues: ValidationIssue[] = [];
        for (const param of params) {
            const value = values[param.name];
            const result = this.validateParameter(value, param);
            if (!result.valid) {
                allIssues.push(...result.issues);
            }
        }
        return buildValidationResult(allIssues);
    }

    /**
     * Validate raw JSON arguments input.
     */
    validateJsonArgs(value: string | undefined | null): ValidationResult {
        return validateJsonObject(value ?? '', 'arguments');
    }

    /**
     * Get a VS Code validateInput-compatible callback for contract ID.
     * Returns undefined when valid, error message string when invalid.
     */
    getContractIdValidator(): (value: string) => string | undefined {
        return (value: string) => this.toValidateInputMessage(this.validateContractId(value));
    }

    /**
     * Get a VS Code validateInput-compatible callback for function name.
     */
    getFunctionNameValidator(): (value: string) => string | undefined {
        return (value: string) => this.toValidateInputMessage(this.validateFunctionName(value));
    }

    /**
     * Get a VS Code validateInput-compatible callback for a parameter.
     */
    getParameterValidator(param: FunctionParameter): (value: string) => string | undefined {
        return (value: string) => this.toValidateInputMessage(this.validateParameter(value, param));
    }

    /**
     * Get a VS Code validateInput-compatible callback for raw JSON args.
     */
    getJsonArgsValidator(): (value: string) => string | undefined {
        return (value: string) => this.toValidateInputMessage(this.validateJsonArgs(value));
    }

    /**
     * Convert ValidationResult to VS Code validateInput return type.
     * Returns undefined if valid, first error message if invalid.
     */
    toValidateInputMessage(result: ValidationResult): string | undefined {
        if (result.valid) return undefined;
        return result.errors[0]?.message ?? result.issues[0]?.message ?? 'Invalid value';
    }
}

/** Singleton instance for convenience. */
let _instance: FormValidationService | null = null;

/**
 * Get the shared FormValidationService instance.
 */
export function getFormValidationService(): FormValidationService {
    if (!_instance) {
        _instance = new FormValidationService();
    }
    return _instance;
}

/**
 * Type validators for form inputs.
 * Validates Soroban/Stellar types: Address, u32, i128, String, Bool, Symbol, etc.
 */

import type { ValidationResult } from '../../types/cliValidation';
import {
    createErrorIssue,
    buildValidationResult,
    validResult,
    invalidResult,
} from './validationResult';

/** Stellar contract ID format: C followed by 55 base32 chars (A-Z, 2-7) */
const CONTRACT_ID_REGEX = /^C[A-Z2-7]{55}$/;

/** Stellar contract ID format (legacy, more permissive): C followed by 55 alphanumeric */
const CONTRACT_ID_REGEX_LEGACY = /^C[A-Z0-9]{55}$/;

/** Function name: identifier starting with letter or underscore */
const FUNCTION_NAME_REGEX = /^[a-zA-Z_][a-zA-Z0-9_]*$/;

/** Soroban unsigned integer max values by type */
const U32_MAX = 2 ** 32 - 1;
const U64_MAX = BigInt('18446744073709551615');
const U128_MAX = BigInt('340282366920938463463374607431768211455');

/** Soroban signed integer bounds */
const I32_MIN = -(2 ** 31);
const I32_MAX = 2 ** 31 - 1;
const I64_MIN = BigInt('-9223372036854775808');
const I64_MAX = BigInt('9223372036854775807');
const I128_MIN = BigInt('-170141183460469231731687303715884105728');
const I128_MAX = BigInt('170141183460469231731687303715884105727');

/**
 * Validate Stellar contract ID (address format).
 * Uses strkey format: C followed by 55 base32 chars (A-Z, 2-7).
 */
export function validateAddress(value: string): ValidationResult {
    if (value === undefined || value === null) {
        return invalidResult('MISSING_VALUE', 'Address is required', { field: 'address' });
    }
    const trimmed = String(value).trim();
    if (trimmed.length === 0) {
        return invalidResult('MISSING_VALUE', 'Address is required', { field: 'address' });
    }
    const normalized = trimmed.toUpperCase();
    if (!CONTRACT_ID_REGEX.test(normalized)) {
        // Fallback: some inputs may use 0-9
        if (!CONTRACT_ID_REGEX_LEGACY.test(normalized)) {
            return invalidResult('INVALID_ADDRESS_FORMAT', 'Invalid contract ID format (should start with C and be 56 characters)', {
                field: 'address',
                receivedValue: trimmed,
                expectedValue: 'C[A-Z2-7]{55}',
            });
        }
    }
    return validResult();
}

/**
 * Validate Soroban function name.
 * Must match: ^[a-zA-Z_][a-zA-Z0-9_]*$
 */
export function validateFunctionName(value: string): ValidationResult {
    if (value === undefined || value === null) {
        return invalidResult('MISSING_VALUE', 'Function name is required', { field: 'functionName' });
    }
    const trimmed = String(value).trim();
    if (trimmed.length === 0) {
        return invalidResult('MISSING_VALUE', 'Function name is required', { field: 'functionName' });
    }
    if (!FUNCTION_NAME_REGEX.test(trimmed)) {
        return invalidResult('INVALID_FUNCTION_NAME', 'Function name must start with a letter or underscore and contain only letters, numbers, and underscores', {
            field: 'functionName',
            receivedValue: trimmed,
        });
    }
    return validResult();
}

/**
 * Check if a required field has a value.
 */
export function validateRequired(value: string | undefined | null, fieldName: string): ValidationResult {
    if (value === undefined || value === null) {
        return invalidResult('MISSING_REQUIRED', `${fieldName} is required`, { field: fieldName });
    }
    const trimmed = String(value).trim();
    if (trimmed.length === 0) {
        return invalidResult('MISSING_REQUIRED', `${fieldName} is required`, { field: fieldName });
    }
    return validResult();
}

/**
 * Normalize Soroban type string from CLI help (e.g. "<ADDRESS>", "u32", "<STRING>").
 */
function normalizeSorobanType(typeStr: string): string {
    if (!typeStr || typeof typeStr !== 'string') return '';
    const t = typeStr.trim().toLowerCase();
    const match = t.match(/<([^>]+)>/);
    if (match) return match[1];
    return t;
}

/**
 * Validate value against a Soroban type from CLI help.
 */
export function validateSorobanType(value: string, typeStr: string, fieldName?: string): ValidationResult {
    const issues: import('../../types/cliValidation').ValidationIssue[] = [];
    const field = fieldName || 'parameter';
    const trimmed = value === undefined || value === null ? '' : String(value).trim();

    const type = normalizeSorobanType(typeStr);

    // Optional empty value
    if (trimmed.length === 0) {
        return validResult();
    }

    // Address
    if (type === 'address') {
        const addrResult = validateAddress(trimmed);
        if (!addrResult.valid) {
            addrResult.issues.forEach((i) => issues.push({ ...i, field }));
        }
    }
    // Unsigned integers
    else if (type === 'u32' || type === 'u64' || type === 'u128') {
        const numResult = validateUnsignedInteger(trimmed, type, field);
        if (!numResult.valid) {
            issues.push(...numResult.issues);
        }
    }
    // Signed integers
    else if (type === 'i32' || type === 'i64' || type === 'i128') {
        const numResult = validateSignedInteger(trimmed, type, field);
        if (!numResult.valid) {
            issues.push(...numResult.issues);
        }
    }
    // Bool
    else if (type === 'bool' || type === 'boolean') {
        try {
            const parsed = JSON.parse(trimmed);
            if (typeof parsed !== 'boolean') {
                issues.push(createErrorIssue('INVALID_BOOL', 'Expected boolean (true or false)', { field, receivedValue: trimmed }));
            }
        } catch {
            issues.push(createErrorIssue('INVALID_JSON', 'Invalid JSON for boolean. Use true or false', { field, receivedValue: trimmed }));
        }
    }
    // String, Symbol
    else if (type === 'string' || type === 'symbol') {
        // Any non-empty string is fine; symbol typically expects non-empty
        if (type === 'symbol' && trimmed.length === 0) {
            issues.push(createErrorIssue('INVALID_SYMBOL', 'Symbol cannot be empty', { field }));
        }
    }
    // Vec, Map, Option, or unknown - attempt JSON parse
    else {
        try {
            JSON.parse(trimmed);
        } catch {
            issues.push(createErrorIssue('INVALID_JSON', `Invalid JSON for type ${type || 'unknown'}`, { field, receivedValue: trimmed }));
        }
    }

    return buildValidationResult(issues);
}

function validateUnsignedInteger(value: string, type: string, field: string): ValidationResult {
    let num: number | bigint;
    try {
        const parsed = JSON.parse(value);
        if (typeof parsed === 'number') {
            if (!Number.isInteger(parsed) || parsed < 0) {
                return invalidResult('INVALID_UINT', `Expected non-negative integer for ${type}`, { field, receivedValue: value });
            }
            num = parsed;
        } else if (typeof parsed === 'string' && /^\d+$/.test(parsed)) {
            num = BigInt(parsed);
        } else {
            return invalidResult('INVALID_UINT', `Expected integer for ${type}`, { field, receivedValue: value });
        }
    } catch {
        return invalidResult('INVALID_JSON', `Invalid number for ${type}`, { field, receivedValue: value });
    }

    if (type === 'u32') {
        if ((typeof num === 'number' ? num : Number(num)) > U32_MAX) {
            return invalidResult('OUT_OF_RANGE', `u32 must be 0 to ${U32_MAX}`, { field, receivedValue: value, expectedValue: `0-${U32_MAX}` });
        }
    } else if (type === 'u64' || type === 'u128') {
        const big = typeof num === 'bigint' ? num : BigInt(num);
        const max = type === 'u64' ? U64_MAX : U128_MAX;
        if (big < 0n || big > max) {
            return invalidResult('OUT_OF_RANGE', `${type} must be 0 to ${max}`, { field, receivedValue: value });
        }
    }

    return validResult();
}

function validateSignedInteger(value: string, type: string, field: string): ValidationResult {
    let num: number | bigint;
    try {
        const parsed = JSON.parse(value);
        if (typeof parsed === 'number') {
            if (!Number.isInteger(parsed)) {
                return invalidResult('INVALID_INT', `Expected integer for ${type}`, { field, receivedValue: value });
            }
            num = parsed;
        } else if (typeof parsed === 'string' && /^-?\d+$/.test(parsed)) {
            num = BigInt(parsed);
        } else {
            return invalidResult('INVALID_INT', `Expected integer for ${type}`, { field, receivedValue: value });
        }
    } catch {
        return invalidResult('INVALID_JSON', `Invalid number for ${type}`, { field, receivedValue: value });
    }

    if (type === 'i32') {
        const n = typeof num === 'number' ? num : Number(num);
        if (n < I32_MIN || n > I32_MAX) {
            return invalidResult('OUT_OF_RANGE', `i32 must be ${I32_MIN} to ${I32_MAX}`, { field, receivedValue: value });
        }
    } else if (type === 'i64' || type === 'i128') {
        const big = typeof num === 'bigint' ? num : BigInt(num);
        const [min, max] = type === 'i64' ? [I64_MIN, I64_MAX] : [I128_MIN, I128_MAX];
        if (big < min || big > max) {
            return invalidResult('OUT_OF_RANGE', `${type} out of range`, { field, receivedValue: value });
        }
    }

    return validResult();
}

/**
 * Helper utilities for building ValidationResult from ValidationIssues.
 */

import type { ValidationResult, ValidationIssue } from '../../types/cliValidation';

/**
 * Create a ValidationIssue with error severity.
 */
export function createErrorIssue(
    code: string,
    message: string,
    options?: { field?: string; receivedValue?: string; expectedValue?: string; suggestion?: string }
): ValidationIssue {
    return {
        severity: 'error',
        code,
        message,
        ...options,
    };
}

/**
 * Build a ValidationResult from an array of issues.
 */
export function buildValidationResult(issues: ValidationIssue[]): ValidationResult {
    const errors = issues.filter((i) => i.severity === 'error');
    const warnings = issues.filter((i) => i.severity === 'warning' || i.severity === 'info');
    return {
        valid: errors.length === 0,
        issues,
        errors,
        warnings,
    };
}

/**
 * Return a valid empty result.
 */
export function validResult(): ValidationResult {
    return {
        valid: true,
        issues: [],
        errors: [],
        warnings: [],
    };
}

/**
 * Return a failed result with a single error.
 */
export function invalidResult(
    code: string,
    message: string,
    options?: { field?: string; receivedValue?: string; expectedValue?: string }
): ValidationResult {
    const issue = createErrorIssue(code, message, options);
    return buildValidationResult([issue]);
}

/**
 * Format validators for form inputs.
 * Validates JSON structures, address format, etc.
 */

import type { ValidationResult } from '../../types/cliValidation';
import { createErrorIssue, buildValidationResult, validResult, invalidResult } from './validationResult';
import { validateAddress as validateAddressFormat } from './typeValidators';

/**
 * Validate that a string is valid JSON and parses to an object (not array or primitive).
 */
export function validateJsonObject(value: string, field?: string): ValidationResult {
    if (value === undefined || value === null) {
        return invalidResult('MISSING_VALUE', 'JSON object is required', { field: field || 'json' });
    }
    const trimmed = String(value).trim();
    if (trimmed.length === 0) {
        return invalidResult('MISSING_VALUE', 'JSON object is required', { field: field || 'json' });
    }
    try {
        const parsed = JSON.parse(trimmed);
        if (typeof parsed !== 'object' || parsed === null || Array.isArray(parsed)) {
            return invalidResult('INVALID_JSON_OBJECT', 'Arguments must be a JSON object', {
                field: field || 'json',
                receivedValue: trimmed.substring(0, 100),
            });
        }
        return validResult();
    } catch (e) {
        const msg = e instanceof Error ? e.message : 'Invalid JSON';
        return invalidResult('INVALID_JSON', `Invalid JSON: ${msg}`, {
            field: field || 'json',
            receivedValue: trimmed.substring(0, 100),
        });
    }
}

/**
 * Validate that a string is valid JSON (any value).
 */
export function validateJsonValue(value: string, field?: string): ValidationResult {
    if (value === undefined || value === null) {
        return validResult(); // Empty is ok for optional
    }
    const trimmed = String(value).trim();
    if (trimmed.length === 0) {
        return validResult();
    }
    try {
        JSON.parse(trimmed);
        return validResult();
    } catch (e) {
        const msg = e instanceof Error ? e.message : 'Invalid JSON';
        return invalidResult('INVALID_JSON', `Invalid JSON: ${msg}`, {
            field: field || 'json',
            receivedValue: trimmed.substring(0, 100),
        });
    }
}

/**
 * Validate Stellar contract address format.
 * Re-exports type validator for consistency.
 */
export function validateAddress(value: string): ValidationResult {
    return validateAddressFormat(value);
}

import { formatError } from '../utils/errorFormatter';

export interface SimulationResult {
    success: boolean;
    result?: any;
    error?: string;
    resourceUsage?: {
        cpuInstructions?: number;
        memoryBytes?: number;
    };
}

export class RpcService {
    private rpcUrl: string;

    constructor(rpcUrl: string) {
        this.rpcUrl = rpcUrl.endsWith('/') ? rpcUrl.slice(0, -1) : rpcUrl;
    }

    async simulateTransaction(
        contractId: string,
        functionName: string,
        args: any[]
    ): Promise<SimulationResult> {
        try {
            const requestBody = {
                jsonrpc: '2.0',
                id: 1,
                method: 'simulateTransaction',
                params: {
                    transaction: {
                        contractId,
                        functionName,
                        args: args.map(arg => ({
                            value: arg
                        }))
                    }
                }
            };

            const response = await fetch(`${this.rpcUrl}/rpc`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(requestBody),
                signal: AbortSignal.timeout(30000)
            });

            if (!response.ok) {
                return {
                    success: false,
                    error: `RPC request failed with status ${response.status}: ${response.statusText}`
                };
            }

            const data: any = await response.json();

            if (data.error) {
                return {
                    success: false,
                    error: data.error.message || 'RPC error occurred'
                };
            }

            const result = data.result || data;

            return {
                success: true,
                result: result.returnValue || result.result || result,
                resourceUsage: result.resourceUsage || result.resource_usage
            };
        } catch (error) {
            const formatted = formatError(error, 'RPC');

            if (error instanceof TypeError && error.message.includes('fetch')) {
                return {
                    success: false,
                    error: `Network error: Unable to reach RPC endpoint at ${this.rpcUrl}. Check your connection and rpcUrl setting.`
                };
            }

            if (error instanceof Error && error.name === 'AbortError') {
                return {
                    success: false,
                    error: 'Request timed out. The RPC endpoint may be slow or unreachable.'
                };
            }

            return {
                success: false,
                error: formatted.message
            };
        }
    }

    async isAvailable(): Promise<boolean> {
        try {
            const response = await fetch(`${this.rpcUrl}/health`, {
                method: 'GET',
                signal: AbortSignal.timeout(5000)
            });
            return response.ok;
        } catch {
            try {
                const response = await fetch(`${this.rpcUrl}/rpc`, {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ jsonrpc: '2.0', id: 1, method: 'getHealth' }),
                    signal: AbortSignal.timeout(5000)
                });
                return response.ok;
            } catch {
                return false;
            }
        }
    }
}

export interface FormattedError {
    title: string;
    message: string;
    details?: string;
}

export function formatError(error: unknown, context?: string): FormattedError {
    let title = 'Error';
    let message = 'An unexpected error occurred';
    let details: string | undefined;

    if (error instanceof Error) {
        message = error.message;
        details = error.stack;

        if (error.message.includes('ENOENT') || error.message.includes('not found')) {
            title = 'Command Not Found';
            message = 'Soroban CLI not found. Make sure it is installed and in your PATH, or configure the cliPath setting.';
        } else if (error.message.includes('ECONNREFUSED') || error.message.includes('network')) {
            title = 'Connection Error';
            message = 'Unable to connect to RPC endpoint. Check your network connection and rpcUrl setting.';
        } else if (error.message.includes('timeout')) {
            title = 'Timeout';
            message = 'Request timed out. The RPC endpoint may be slow or unreachable.';
        } else if (error.message.includes('invalid') || error.message.includes('Invalid')) {
            title = 'Invalid Input';
        }
    } else if (typeof error === 'string') {
        message = error;
    }

    if (context) {
        title = `${title} (${context})`;
    }

    return {
        title,
        message,
        details
    };
}

export function formatCliError(stderr: string): string {
    const lines = stderr.split('\n').filter(line => line.trim().length > 0);

    for (const line of lines) {
        if (line.toLowerCase().includes('error') || line.toLowerCase().includes('failed')) {
            return line.trim();
        }
    }

    return lines[0] || stderr.trim() || 'Unknown CLI error';
}

import { execFile, exec, ExecFileOptions } from 'child_process';
import { promisify } from 'util';
import { formatCliError } from '../utils/errorFormatter';
import * as os from 'os';
import * as path from 'path';

const execFileAsync = promisify(execFile);
const execAsync = promisify(exec);

export type ExecFileFunction = (
    file: string,
    args: ReadonlyArray<string>,
    options: ExecFileOptions
) => Promise<{ stdout: string; stderr: string }>;

function getEnvironmentWithPath(): NodeJS.ProcessEnv {
    const env = { ...process.env };
    const homeDir = os.homedir();
    const cargoBin = path.join(homeDir, '.cargo', 'bin');

    const additionalPaths = [
        cargoBin,
        path.join(homeDir, '.local', 'bin'),
        '/usr/local/bin',
        '/opt/homebrew/bin',
        '/opt/homebrew/sbin'
    ];

    const currentPath = env.PATH || env.Path || '';
    env.PATH = [...additionalPaths, currentPath].filter(Boolean).join(path.delimiter);
    env.Path = env.PATH;

    return env;
}

export interface SimulationResult {
    success: boolean;
    result?: any;
    error?: string;
    resourceUsage?: {
        cpuInstructions?: number;
        memoryBytes?: number;
    };
}

export class SorobanCliService {
    private cliPath: string;
    private source: string;
    private execFile: ExecFileFunction;

    constructor(
        cliPath: string,
        source: string = 'dev',
        executor?: ExecFileFunction
    ) {
        this.cliPath = cliPath;
        this.source = source;
        this.execFile = executor || execFileAsync;
    }

    async simulateTransaction(
        contractId: string,
        functionName: string,
        args: any[],
        network: string = 'testnet'
    ): Promise<SimulationResult> {
        try {
            const commandParts = [
                this.cliPath,
                'contract',
                'invoke',
                '--id', contractId,
                '--source', this.source,
                '--network', network,
                '--'
            ];

            commandParts.push(functionName);

            if (args.length > 0 && typeof args[0] === 'object' && !Array.isArray(args[0])) {
                const argObj = args[0];
                for (const [key, value] of Object.entries(argObj)) {
                    commandParts.push(`--${key}`);
                    if (typeof value === 'object') {
                        commandParts.push(JSON.stringify(value));
                    } else {
                        commandParts.push(String(value));
                    }
                }
            } else {
                for (const arg of args) {
                    if (typeof arg === 'object') {
                        commandParts.push(JSON.stringify(arg));
                    } else {
                        commandParts.push(String(arg));
                    }
                }
            }

            const env = getEnvironmentWithPath();

            const { stdout, stderr } = await this.execFile(
                commandParts[0],
                commandParts.slice(1),
                {
                    env: env,
                    maxBuffer: 10 * 1024 * 1024,
                    timeout: 30000
                }
            );

            if (stderr && stderr.trim().length > 0) {
                if (stderr.toLowerCase().includes('error') || stderr.toLowerCase().includes('failed')) {
                    return {
                        success: false,
                        error: formatCliError(stderr)
                    };
                }
            }

            try {
                const output = stdout.trim();
                try {
                    const parsed = JSON.parse(output);
                    const resourceUsage = parsed.resource_usage || parsed.resourceUsage;
                    return {
                        success: true,
                        result: parsed.result || parsed.returnValue || parsed,
                        resourceUsage: resourceUsage ? {
                            cpuInstructions: resourceUsage.cpu_instructions || resourceUsage.cpuInstructions || parsed.cpu_instructions,
                            memoryBytes: resourceUsage.memory_bytes || resourceUsage.memoryBytes || parsed.memory_bytes
                        } : (parsed.cpu_instructions ? {
                            cpuInstructions: parsed.cpu_instructions,
                            memoryBytes: parsed.memory_bytes
                        } : undefined)
                    };
                } catch {
                    const jsonMatch = output.match(/\{[\s\S]*\}/);
                    if (jsonMatch) {
                        const parsed = JSON.parse(jsonMatch[0]);
                        const resourceUsage = parsed.resource_usage || parsed.resourceUsage;
                        return {
                            success: true,
                            result: parsed.result || parsed.returnValue || parsed,
                            resourceUsage: resourceUsage ? {
                                cpuInstructions: resourceUsage.cpu_instructions || resourceUsage.cpuInstructions || parsed.cpu_instructions,
                                memoryBytes: resourceUsage.memory_bytes || resourceUsage.memoryBytes || parsed.memory_bytes
                            } : (parsed.cpu_instructions ? {
                                cpuInstructions: parsed.cpu_instructions,
                                memoryBytes: parsed.memory_bytes
                            } : undefined)
                        };
                    }
                    return {
                        success: true,
                        result: output
                    };
                }
            } catch (parseError) {
                return {
                    success: true,
                    result: stdout.trim()
                };
            }
        } catch (error) {
            const errorMessage = error instanceof Error ? error.message : 'Unknown error';

            if (errorMessage.includes('ENOENT') || errorMessage.includes('not found')) {
                return {
                    success: false,
                    error: `Stellar CLI not found at "${this.cliPath}". Make sure it is installed and in your PATH, or configure the stellarSuite.cliPath setting.`
                };
            }

            return {
                success: false,
                error: `CLI execution failed: ${errorMessage}`
            };
        }
    }

    async isAvailable(): Promise<boolean> {
        try {
            const env = getEnvironmentWithPath();
            await this.execFile(this.cliPath, ['--version'], { env: env, timeout: 5000 });
            return true;
        } catch {
            return false;
        }
    }

    static async findCliPath(): Promise<string | null> {
        const commonPaths = [
            'stellar',
            path.join(os.homedir(), '.cargo', 'bin', 'stellar'),
            '/usr/local/bin/stellar',
            '/opt/homebrew/bin/stellar',
            '/usr/bin/stellar'
        ];

        const env = getEnvironmentWithPath();
        for (const cliPath of commonPaths) {
            try {
                if (cliPath === 'stellar') {
                    await execAsync('stellar --version', { env: env, timeout: 5000 });
                    return 'stellar';
                } else {
                    await execFileAsync(cliPath, ['--version'], { env: env, timeout: 5000 });
                    return cliPath;
                }
            } catch {
            }
        }
        return null;
    }
}

/**
 * Range validators for form inputs.
 * Validates numeric ranges and string length constraints.
 */

import type { ValidationResult } from '../../types/cliValidation';
import { createErrorIssue, buildValidationResult, validResult, invalidResult } from './validationResult';

/**
 * Validate a number is within optional min/max bounds.
 */
export function validateNumberRange(
    value: number,
    options?: { min?: number; max?: number; field?: string }
): ValidationResult {
    const { min, max, field = 'value' } = options || {};
    if (min !== undefined && value < min) {
        return invalidResult('BELOW_MIN', `Value must be at least ${min}`, {
            field,
            receivedValue: String(value),
            expectedValue: `min ${min}`,
        });
    }
    if (max !== undefined && value > max) {
        return invalidResult('ABOVE_MAX', `Value must be at most ${max}`, {
            field,
            receivedValue: String(value),
            expectedValue: `max ${max}`,
        });
    }
    return validResult();
}

/**
 * Validate string length is within optional min/max.
 */
export function validateStringLength(
    value: string,
    options?: { min?: number; max?: number; field?: string }
): ValidationResult {
    const { min, max, field = 'value' } = options || {};
    const len = value.length;
    if (min !== undefined && len < min) {
        return invalidResult('TOO_SHORT', `Value must be at least ${min} characters`, {
            field,
            expectedValue: `min length ${min}`,
        });
    }
    if (max !== undefined && len > max) {
        return invalidResult('TOO_LONG', `Value must be at most ${max} characters`, {
            field,
            expectedValue: `max length ${max}`,
        });
    }
    return validResult();
}

/**
 * Get min/max bounds for a Soroban integer type.
 */
export function getSorobanIntegerBounds(type: string): { min?: number; max?: number } {
    const t = type.toLowerCase();
    switch (t) {
        case 'u32':
            return { min: 0, max: 2 ** 32 - 1 };
        case 'i32':
            return { min: -(2 ** 31), max: 2 ** 31 - 1 };
        case 'u64':
        case 'u128':
        case 'i64':
        case 'i128':
            // BigInt ranges - caller should use BigInt validation
            return {};
        default:
            return {};
    }
}
